import React, { Component } from 'react';
import { Container } from 'reactstrap';
import { NavMenu } from './NavMenu';
import 'react-widgets/dist/css/react-widgets.css';
import { DropdownList } from 'react-widgets'

export class Input extends Component {

    render() {
        return (     
            <section id="contact" style={{ height: 500 + 'px' }}>




                <div class="social column">
                    <h3>Магазин</h3>
                    <NameForm/>
                    <form>
                        <label>
                            :
                         <input type="text" name="name" />
                        </label>                   
                    </form>
                    <form>
                        <label>
                            Имя:
                         <input type="text" name="name" />
                        </label>
                       
                        <form>
                            <label>
                                Имя:
                         <input type="text" name="name" />
                            </label>
                          
                        </form>
                    </form>
                    <Example />
                    <Example />
                    <input type="submit" value="Отправить" />
                </div>
                <div className="row">
                    <div className="bg-dark"></div>
                </div>
               

                <div class=" column">
                    <Example/>
                  
                </div>
                <div className="row">
                    <div className="bg-dark"></div>
                </div>
            </section>
          
        );
    }
}
class NameForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: '', name: ''};

        this.handleChange1 = this.handleChange1.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange1(event) {
        this.setState({ value: event.target.value});
    }
    handleChange2(event) {
        this.setState({ name: event.target.name});
    }

    handleSubmit(event) {
        alert('Отправленное имя: ' + this.state.value + "    " + this.state.name);
        event.preventDefault();
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                   Value:
                  <input type="text" value={this.state.value} onChange={this.handleChange1} />
                </label>
                <label>
                    Имя:
                  <input type="text" name={this.state.name} onChange={this.handleChange2} />
                </label>
                <input type="submit" value="Отправить" />
            </form>
        );
    }
}


let colors = ['orange', 'red', 'blue', 'purple'];

class Example extends React.Component {
    constructor(...args) {
        super(...args)
        this.state = { value: 'orange' }
    }
    render() {
        return (
            <DropdownList
                data={colors}
                value={this.state.value}
                onChange={value => this.setState({ value })}
            />
        )
    }
}
