import React, { Component } from 'react';
import Gallery from 'react-photo-gallery'; 

export default class StoreGallery extends React.Component {
    componentDidMount() {
        fetch("https://localhost:44393/api/Store/")
            .then((response) => {
                return response.json();
            })
            .then(data => {
                let itemsFromApi = data.map(item => { return { value: item.id, display: item.logo, name: item.name } })
                this.setState({ itemsStore: [{ value: '', display: '', name: '' }].concat(itemsFromApi) });
            }).catch(error => {
                console.log(error);
            });
    }
    render() {
        return (
            <Gallery photos={PHOTO_SET} />
        );
    }
}


const PHOTO_SET = [
           
    {
        src: 'https://www.meme-arsenal.com/memes/6d860d9f0bb1ded1e047163c4ca75f14.jpg',
        width: 2,
        height: 1
    },
    {
        src: 'https://www.meme-arsenal.com/memes/6d860d9f0bb1ded1e047163c4ca75f14.jpg',
        width: 1,
        height: 2
    }
];

export class Counter extends Component {
  static displayName = Counter.name;


    constructor(props) {
        super(props);
        this.state = {
            forecasts: [],
            loading: true,
            formValues: {},
            tech: 'select',
            Variety: [],
            itemsStore: [],
            selected: "",
        };
    }

    componentDidMount() {
        fetch("https://localhost:44393/api/Store/")
            .then((response) => {
                return response.json();
            })
            .then(data => {
                let itemsFromApi = data.map(item => { return { value: item.id, display: item.logo } })
                this.setState({ itemsStore: [{ value: '', display: ''}].concat(itemsFromApi) });
            }).catch(error => {
                console.log(error);
            });
    }

    static renderForecastsTable(forecasts) {
        return (
            <table className='table table-striped' aria-labelledby="tabelLabel">
                <thead>
                    <tr>
                        <th>Names</th>
                        <th>Logos</th>
                    </tr>
                </thead>
                <tbody>
                    {forecasts.map(forecast =>
                        <tr key={forecast.name}>
                            <td>{forecast.name}</td>
                            <td>{forecast.logo}</td>
                        </tr>
                    )}
                </tbody>
            </table>
        );
    }
    render() {
   
        return (
          <div>
          <section id="galleries">
				<div class="gallery">             
				<header>
                      <h1>Gallery</h1>
                      <ul class="tabs">
                          <li><a href="#" data-tag="all" class="button active">All</a></li>
                          <li><a href="#" data-tag="people" class="button">People</a></li>
                          <li><a href="#" data-tag="place" class="button">Places</a></li>
                          <li><a href="#" data-tag="thing" class="button">Things</a></li>
                      </ul>
                </header>

                  <div class="content">
                     
                        {this.state.itemsStore.map((item) =>
                            <option key={item.value} value={item.value}>
                                <div class="media all people">                                   
                                    <img  alt="" title="This right here is a caption." >{item.display}</img>
                                      </div>
                            </option>)
                        }                       
                     
                     
                      <div class="media all people">
                          <a href="images/fulls/01.jpg"><img src={this.state.itemsStore} alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all place">
                          <a href="images/fulls/05.jpg"><img src="images/thumbs/05.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all thing">
                          <a href="images/fulls/09.jpg"><img src="images/thumbs/09.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all people">
                          <a href="images/fulls/02.jpg"><img src="images/thumbs/02.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all place">
                          <a href="images/fulls/06.jpg"><img src="images/thumbs/06.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all thing">
                          <a href="images/fulls/10.jpg"><img src="images/thumbs/10.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all people">
                          <a href="images/fulls/03.jpg"><img src="images/thumbs/03.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all place">
                          <a href="images/fulls/07.jpg"><img src="images/thumbs/07.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all thing">
                          <a href="images/fulls/11.jpg"><img src="images/thumbs/11.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all people">
                          <a href="images/fulls/04.jpg"><img src="images/thumbs/04.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all place">
                          <a href="images/fulls/08.jpg"><img src="images/thumbs/08.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                      <div class="media all thing">
                          <a href="images/fulls/12.jpg"><img src="images/thumbs/12.jpg" alt="" title="This right here is a caption." /></a>
                      </div>
                  </div>
              </div>
          </section>
          <section id="contact">

              <div class="social column">
                </div>
                  <div class="column">
                      <StoreGallery/>
              </div>
          </section>
      </div>

    );
  }
    async populateStoreData() {
        const response = await fetch('api/Store');
        const data = await response.json();
        this.setState({ forecasts: data, loading: false });
    }
}
